package HelloWorld;

public class iteration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		int j = 1;
		while (i <= 5) {
			System.out.println("hello sarath");
			i++;

		}

		do {
			System.out.println("Hello clm");
			j++;
		} while (j <= 5);

		for (int k = 1; k <= 5; k++) {
			System.out.println("Hello pattu");
		}
	}

}
