package HelloWorld;

public class iteration_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j;
		
		
		for (i=0;i<6;i++) {
			int num=1;
			for (j=0;j<=i;j++) {
				System.out.print(num + " ");
				num++;
			}
			System.out.println(" ");
		}
		
	    
   
	}

}
